"use client"

import * as React from "react"
import { Check, ChevronsUpDown } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command"
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover"

interface Agent {
  id: string
  name: string
  description?: string
}

interface AgentPickerProps {
  agents: Agent[]
  value?: string
  onValueChange?: (value: string) => void
  placeholder?: string
  className?: string
  open?: boolean
  onOpenChange?: (open: boolean) => void
}

function AgentPicker({
  agents,
  value,
  onValueChange,
  placeholder = "Select an agent...",
  className,
  open,
  onOpenChange,
}: AgentPickerProps) {
  const [internalOpen, setInternalOpen] = React.useState(false)
  const isControlled = open !== undefined
  const isOpen = isControlled ? open : internalOpen
  const setIsOpen = isControlled ? onOpenChange : setInternalOpen

  const selectedAgent = agents.find((a) => a.id === value)

  return (
    <Popover open={isOpen} onOpenChange={setIsOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={isOpen}
          className={cn("w-full justify-between", className)}
        >
          {selectedAgent ? (
            <span className="truncate">{selectedAgent.name}</span>
          ) : (
            placeholder
          )}
          <ChevronsUpDown className="ml-2 size-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[var(--radix-popover-trigger-width)] p-0">
        <Command>
          <CommandInput placeholder="Search agents..." />
          <CommandList>
            <CommandEmpty>No agent found.</CommandEmpty>
            <CommandGroup>
              {agents.map((agent) => (
                <CommandItem
                  key={agent.id}
                  value={agent.id}
                  onSelect={() => {
                    onValueChange?.(agent.id)
                    setIsOpen?.(false)
                  }}
                  className="flex items-center justify-between"
                >
                  <span>{agent.name}</span>
                  <Check
                    className={cn(
                      "ml-2 size-4 shrink-0",
                      value === agent.id ? "opacity-100" : "opacity-0"
                    )}
                  />
                </CommandItem>
              ))}
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  )
}

export { AgentPicker, type Agent }

"use client"

import { useState } from "react"

import { AgentPicker, type Agent } from "@/components/ui/agent-picker"

const agents: Agent[] = [
  {
    id: "lenny-language-tutor",
    name: "Lenny Language Tutor",
    description:
      "Your AI for learning new languages with interactive lessons and real-time feedback.",
  },
  {
    id: "data-analyst",
    name: "Data Analyst",
    description: "Expert in data analysis and visualization.",
  },
  {
    id: "code-helper",
    name: "Code Helper",
    description: "Assists with programming and debugging.",
  },
  {
    id: "creative-writer",
    name: "Creative Writer",
    description: "Helps with creative writing and storytelling.",
  },
]

export default function AgentPickerDemo() {
  const [selectedAgent, setSelectedAgent] = useState<string>(
    "lenny-language-tutor"
  )
  const [open, setOpen] = useState(false)

  return (
    <div className="w-full max-w-lg">
      <AgentPicker
        agents={agents}
        value={selectedAgent}
        onValueChange={(value) => {
          setSelectedAgent(value)
          setOpen(false)
        }}
        open={open}
        onOpenChange={setOpen}
        placeholder="Select an agent..."
      />
      {selectedAgent && (
        <div className="mt-4 rounded-lg border p-4">
          <p className="text-sm font-medium">Selected Agent:</p>
          <p className="mt-2 text-lg font-semibold">
            {agents.find((a) => a.id === selectedAgent)?.name}
          </p>
          {agents.find((a) => a.id === selectedAgent)?.description && (
            <p className="text-muted-foreground mt-2 text-sm">
              {agents.find((a) => a.id === selectedAgent)?.description}
            </p>
          )}
        </div>
      )}
    </div>
  )
}
