"use client"

import * as React from "react"
import { Check } from "lucide-react"

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import {
  MicButton,
  type MicButtonState,
} from "@/components/ui/mic-button"

export interface SelectItem {
  value: string
  label: string
}

export interface MicSelectorProps {
  items: SelectItem[]
  value?: string
  onValueChange?: (value: string) => void
  disabled?: boolean
  className?: string
  /**
   * Current state of the mic selector (idle, listening, processing, error)
   * @default "idle"
   */
  state?: MicButtonState
  /**
   * Show error badge when in error state (indicates permission denied)
   * @default false
   */
  showErrorBadge?: boolean
}

export function MicSelector({
  items,
  value,
  onValueChange,
  disabled = false,
  className,
  state = "idle",
  showErrorBadge = false,
}: MicSelectorProps) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <MicButton
          state={state}
          showErrorBadge={showErrorBadge}
          className={className}
        />
      </DropdownMenuTrigger>
      <DropdownMenuContent align="center" side="top" className="w-72">
        {items.length === 0 ? (
          <div className="text-muted-foreground py-6 text-center text-sm">
            No items available
          </div>
        ) : (
          items.map((item) => (
            <DropdownMenuItem
              key={item.value}
              onClick={() => onValueChange?.(item.value)}
              className="flex items-center justify-between"
              disabled={state === "processing" || state === "error"}
            >
              <span className="truncate">{item.label}</span>
              {value === item.value && (
                <Check className="h-4 w-4 flex-shrink-0" />
              )}
            </DropdownMenuItem>
          ))
        )}
      </DropdownMenuContent>
    </DropdownMenu>
  )
}
